import React, { Component } from 'react';
import * as apiCurrency from '../services/currencyApi';

class Forms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      allCurrencies: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    apiCurrency.fetchCurrency()
      .then((currencies) => this.setState({ allCurrencies: currencies }));
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  currencyInputDropDown(allCurrencies) {
    return (
      <select
        data-testid="currency-input"
        onChange={ this.handleChange }
      >
        { allCurrencies.map((currency) => (
          <option
            key={ currency.code }
            data-testid={ currency.code }
          >
            {currency.code}
          </option>
        )) }
      </select>);
  }

  render() {
    const { allCurrencies } = this.state;
    return (
      <forms>
        <input type="number" data-testid="value-input" />
        <input type="text" data-testid="description-input" />
        { this.currencyInputDropDown(allCurrencies) }
        <select data-testid="method-input" name="paymentMethod">
          <option>Dinheiro</option>
          <option>Cartão de crédito</option>
          <option>Cartão de débito</option>
        </select>
      </forms>
    );
  }
}

export default Forms;
