// Esse reducer será responsável por tratar o todas as informações relacionadas as despesas

import { SET_TOTAL, SET_CURRENCIES, ADD_EXPENSES } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  expanses: [],
  total: '',
};

const wallet = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SET_TOTAL:
    return { ...state, total: action.total };
  case SET_CURRENCIES:
    return { ...state, currencies: action.currencies };
  case ADD_EXPENSES:
    return { ...state, expanses: [...state.expenses, action.expenses] };
  default:
    return state;
  }
};

export default wallet;
