import React, { Component } from 'react';
import { connect } from 'react-redux';
import { actionCurrencies } from '../actions';
import fetchCurrency from '../services/currencyApi';

class Forms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      allCurrencies: [],
      total: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    fetchCurrency()
      .then((currencies) => this.setState({ allCurrencies: currencies }));
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  currencyInputDropDown(allCurrencies) {
    return (
      <select
        data-testid="currency-input"
        onChange={ this.handleChange }
      >
        { allCurrencies.map((currency) => (
          <option
            key={ currency.code }
            data-testid={ currency.code }
          >
            {currency.code}
          </option>
        )) }
      </select>);
  }

  render() {
    const { allCurrencies } = this.state;
    return (
      <forms>
        <input type="number" data-testid="value-input" />
        {/* preciso pegar inputValue */}
        <input type="text" data-testid="description-input" />
        { this.currencyInputDropDown(allCurrencies) }
        <select data-testid="method-input" name="paymentMethod">
          <option>Dinheiro</option>
          <option>Cartão de crédito</option>
          <option>Cartão de débito</option>
        </select>
        <select data-testid="tag-input" name="paymentMethod">
          <option>Alimentação</option>
          <option>Lazer</option>
          <option>Trabalho</option>
          <option>Saúde</option>
        </select>
        <button type="button">Adicionar despesa</button>
      </forms>
    );
  }
}
const mapStateToProps = (state) => ({
  src: state.imagePath,
  isFetching: state.isFetching});

const mapDispatchToProps = (dispatch) => ({
  getCurrency: (total) => dispatch(actionCurrencies(total)) });

export default connect(mapStateToProps, mapDispatchToProps)(Forms);
