import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { addExpenses } from '../actions';

class ExpensesList extends Component {
  constructor(props) {
    super(props);
    this.btnDelete = this.btnDelete.bind(this);
    // this.btnEdit = this.btnEdit.bind(this);
  }

  btnDelete(id) {
    const { expenses, setExpenses } = this.props;
    const filter = expenses.filter(({ filteredId }) => filteredId !== id);
    setExpenses(filter);
  }

  render() {
    const { expenses } = this.props;
    return (
      <table>
        <thead>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
        </thead>
        <tbody>
          { expenses.map((
            { id, value, description, currency, method, tag, exchangeRates }, index,
          ) => (
            <tr key={ index }>
              <td>{description}</td>
              <td>{tag}</td>
              <td>{method}</td>
              <td>{value}</td>
              <td>{exchangeRates[currency].name}</td>
              <td>
                {parseFloat(exchangeRates[currency].ask).toFixed(2)}
              </td>
              <td>
                {(value * exchangeRates[currency].ask).toFixed(2)}
              </td>
              <td>Real</td>
              <button
                type="button"
                data-testid="delete-btn"
                onClick={ this.btnDelete(id) }
              >
                Excluir
              </button>
              <button type="button" data-testid="edit-btn">Editar</button>
            </tr>
          )) }
        </tbody>
      </table>
    );
  }
}

ExpensesList.propTypes = {
  setExpenses: PropTypes.func.isRequired,
  expenses: PropTypes.shape({
    map: PropTypes.func.isRequired,
    filter: PropTypes.func.isRequired,
  }).isRequired,
};

const mapDispatchToProps = (dispatch) => ({
  setExpenses: (expenses) => dispatch(addExpenses(expenses)),
});

const mapStateToProps = (state) => ({ expenses: state.wallet.expenses });

export default connect(mapStateToProps, mapDispatchToProps)(ExpensesList);
