import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { login } from '../actions';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
    };
    this.checkPassword = this.checkPassword.bind(this);
    this.checkMail = this.checkMail.bind(this);
    this.validate = this.validate.bind(this);
    this.clickHandler = this.clickHandler.bind(this);
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  checkMail(email) {
    const emailRegex = /^([\w.-]+)@([\w-]+)((.(\w){2,3})+)$/;
    if (emailRegex.test(email)) {
      return true;
    }
  }

  checkPassword(password) {
    const min = 6;
    if (password.length > min) {
      return true;
    }
  }

  validate() {
    const { password, email } = this.state;
    if (this.checkPassword(password) && this.checkMail(email) === true) {
      return false;
    }
  }

  clickHandler() {
    const { history, getEmail } = this.props;
    const { email } = this.state;
    getEmail(email);
    if (this.validSubmit()) history.push('/carteira');
  }

  render() {
    const { email, password } = this.state;
    return (
      <div>
        <input
          placeholder="Insert your E-mail"
          name="email"
          type="email"
          value={ email }
          data-testid="email-input"
          onChange={ this.handleChange }
          isRequired
        />
        <input
          placeholder="Insert Password"
          name="password"
          type="password"
          value={ password }
          data-testid="password-input"
          isRequired
          onChange={ this.handleChange }
        />
        <Link to="/carteira">
          <button type="button" disabled={ this.validate() }>Entrar</button>
        </Link>
      </div>
    );
  }
}

export default Login;
