import fetchCurrency from '../services/currencyApi';

export const LOGIN = 'LOGIN';
export const TOTAL = 'TOTAL';

export const login = (email) => ({ type: LOGIN, email });
export const actionCurrencies = (total) => ({ type: TOTAL, total });

// resultado para passar pra store
// loading await fetch
const REQUEST_CURRENCY = 'REQUEST_CURRENCY';
const RECEIVE_CURRENCY = 'RECEIVE_CURRENCY';

const requestCurrency = () => ({ type: REQUEST_CURRENCY });

const receiveCurrency = (allCurrencies) => ({ type: RECEIVE_CURRENCY, allCurrencies });

// export const thunkTeste = () => (async (dispatch) => {(
//   fetchCurrency().then((currencies) => (
//     dispatch(currencies)
//   ))
// )}
// );

export function thunkTeste() {
  return (dispatch) => {
    dispatch(requestCurrency());
    return fetchCurrency()
      .then((currency) => dispatch(
        receiveCurrency(currency),
      ));
  };
}

