import React from 'react';
import { Link } from 'react-router-dom';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
    }
    this.checkPassword = this.checkPassword.bind(this);
    this.checkMail = this.checkMail.bind(this);
    this.validate = this.validate.bind(this);
  }

  checkMail(event) {
    const emailRegex = /\S+@\S+\.\S+/;
    const email = event.target.value;
    if (emailRegex.test(email)) {
      return false;
    }
    return true;
  }

  checkPassword(event) {
    const passCounter = event.target.value;
    const min = 6;
    if (passCounter.length < min) {
      return false;
    }
    return true;
  }

  validate() {
    if (this.checkPassword && this.checkMail === true) {
      return false;
    }
  }

  render() {
    const { email, password } = this.state;
    return (
      <div>
        <input
          placeholder="Insert your E-mail"
          name="email"
          type="email"
          data-testid="email-input"
          onChange={ this.checkMail }
          value={ email }
          required
        />
        <input
          placeholder="Insert Password"
          name="password"
          type="password"
          data-testid="password-input"
          value={ password }
          required
          onChange={ this.checkPassword }
        />
        <Link to="/carteira">
          <button type="button" disabled={ () => (validate()) }>Entrar</button>
        </Link>
      </div>
    );
  }
}

export default Login;
